import unittest
from sssa import sssa

class TestStringMethods(unittest.TestCase):
    def test_create_combine(self):
        values = ["N17FigASkL6p1EOgJhRaIquQLGvYV0", "0y10VAfmyH7GLQY6QccCSLKJi8iFgpcSBTLyYOGbiYPqOpStAf1OYuzEBzZR", ""]
        minimum = [4, 6, 20]
        shares = [5, 100, 100]

        sss = sssa()

        for index,value in enumerate(values):
            self.assertEqual(sss.combine(sss.create(minimum[index], shares[index], value)), value)

if __name__ == '__main__':
    unittest.main()
